mapper.select:
select 

from roomtype
where hotelid = #{hotelid,jdbcType=INTEGER}
  and roomtype = #{roomtype,jdbcType=VARCHAR}

mapper.delete:
delete from roomtype
where hotelid = #{hotelid,jdbcType=INTEGER}
  and roomtype = #{roomtype,jdbcType=VARCHAR}

mapper.update:
update roomtype


    num = #{num,jdbcType=INTEGER},


    price = #{price,jdbcType=DOUBLE},


where hotelid = #{hotelid,jdbcType=INTEGER}
  and roomtype = #{roomtype,jdbcType=VARCHAR}

update roomtype
set num = #{num,jdbcType=INTEGER},
  price = #{price,jdbcType=DOUBLE}
where hotelid = #{hotelid,jdbcType=INTEGER}
  and roomtype = #{roomtype,jdbcType=VARCHAR}
